.custom-checkbox {
  /* Hide the default checkbox */
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  width: 20px; /* Set custom width */
  height: 20px; /* Set custom height */
  border: 2px solid #4a5568; /* Add border */
  border-radius: 4px; /* Round the corners */
  transition: all 0.3s ease; /* Add transition for animation */
  cursor: pointer; /* Change cursor to pointer */
}

.custom-checkbox:checked {
  background-color: #4a5568; /* Change background color when checked */
  border-color: #4a5568; /* Change border color when checked */
}

.custom-checkbox:checked + label::before {
  content: "\2713"; /* Unicode character for checkmark */
  font-size: 16px; /* Set font size */
  color: white; /* Change color of checkmark */
  display: inline-block;
  width: 20px;
  height: 20px;
  line-height: 20px; /* Center the checkmark vertically */
  text-align: center; /* Center the checkmark horizontally */
  background-color: #4a5568; /* Change background color of checkmark */
  border-radius: 2px; /* Round the checkmark */
  transition: all 0.3s ease; /* Add transition for animation */
}


</style>



